import React, { useState, useEffect } from 'react';
import StartGame from '../Components/StartGame'

function Home() {
    const [users, setUsers] = useState([]);
    const [player1, setPlayer1] = useState(0);
    const [player2, setPlayer2] = useState(0);

    const fetchData = () => {
        fetch("http://127.0.0.1:8000/").then(response => {return response.json()}).then(data => { setUsers(data) });
    }

    useEffect(() => {
        fetchData()
    }, []);

    return (
        <>
            <div>
                {users.length > 0 && (
                <ul>
                  {users.map(user => (
                      <li key={user.id}>name: {user.name} | wins: {user.wins}</li>
                  ))}
                </ul>
                )}
            </div>
            <div>
                <form onSubmit={HandleSubmit1}>
                    <input type="text">Player 1</input>
                    <button type="submit">SelectPlayer1</button>
                </form>
                <form onSubmit={HandleSubmit2}>
                    <input type="text">Player 2</input>
                    <button type="submit">Select player2</button>
                </form>
                <form>
                    <input type="text">Who is serving?</input>
                </form>
        
        
    </div>
        </>
  )
}

export default Home
