import logo from './logo.svg';
import './App.css';
import React, { useState, useEffect } from 'react';

function App() {
  const [player1Score, setPlayer1Score] = useState(0);
  const [player2Score, setPlayer2Score] = useState(0);
  const [server, setServer] = useState(1);
  const [round, setRound] = useState(1);
  const [winner, setWinner] = useState("");

  function serverSelect(){
    if((player1Score+player2Score)%2 != 0 && server == 1){
      setServer(2);
      setRound(round + 1);
    }
    else if((player1Score+player2Score)%2 != 0 && server == 2){
      setServer(1);
      setRound(round + 1);
    }
  }

  function detWinner(){
    if((player1Score == 11 || player2Score == 11) && (player1Score-player2Score >= 2 || player1Score-player2Score <= 2)){
      if(player1Score>player2Score){
        setWinner("Player 1");
      } else { setWinner("Player 2"); }
    }
  }


  return (
    <>
      <div>Game</div>
      <h5>Round: {round}</h5>
      <h5>Server: Player {server}</h5>
      <h5>Player 1 Score: {player1Score}</h5>
      <button onClick={() => {setPlayer1Score(player1Score + 1); serverSelect(); detWinner()}}>Player 1 score</button>
      <h5>Player 2 Score: {player2Score}</h5>
      <button onClick={() => {setPlayer2Score(player2Score + 1); serverSelect(); detWinner()}}>Player 2 score</button>
    </>
  );
}

export default App;
